import React from 'react'
import Logo from "../images/AccessAutonomyLogoBlack.png"
import "../styles/style.scss"
import { Link } from "gatsby" 
function nav() {
   return (
      <div id={"nav"}>
      <img id={"logo"} src={Logo} alt={"Access Autonomy Logo"} />
         <div id={"items-container"}>
            <ul id={"items"}>
              <Link activeStyle={{ color: "red" }} to="#projects"><li>Projects</li></Link>
                 <Link activeStyle={{ color: "red" }} to="#services"><li>Services</li></Link>
                 <Link activeStyle={{ color: "red" }} to="#contacts"><li>Contacts</li></Link>
            </ul>
         </div>
      </div>
   );
}

export default nav;